package net.jeeshop.services.front.product;import java.util.List;import net.jeeshop.core.Services;import net.jeeshop.services.front.catalog.bean.Catalog;import net.jeeshop.services.front.product.bean.Product;import net.jeeshop.services.front.product.bean.ProductStockInfo;public interface ProductService extends Services<Product> {	/**	 * 商品上架	 * @param ids	 */	void upGoods(String[] ids);		/**	 * 商品下架	 * @param ids	 */	void downGoods(String[] ids);	/**	 * 根据商品关键字搜索商品列表	 * @param key	 * @return	 */	List<Product> search(Product e);	/**	 * 查询商品参数列表	 * @param id	 * @return	 */	List<Product> selectParameterList(String id);	/**	 * 加载商品库存列表	 * @param product	 * @return	 */	List<ProductStockInfo> selectStockList(Product product);		/**	 * 加载商品介绍信息	 * @param product	 * @return	 */	List<Product> selectListProductHTML(Product product);		/**	 * 根据商品ID集合加载商品信息	 * @param p	 * @return	 */	List<Product> selectProductListByIds(Product p);	/**	 * 查询热门搜索的商品	 * @param list	 * @return	 */	List<Product> selectHotSearch(Product p);	/**	 * 加载热卖商品列表，此数据将会在门户的超级菜单上显示出来。 	 * @param product	 * @return	 */	List<Product> loadHotProductShowInSuperMenu(Product product);	/**	 * 更新商品的浏览次数	 * @param p	 */	void updateHit(Product p);	/**	 * 加载页面左侧商品列表	 * @param p	 * @return	 */	List<Product> selectPageLeftHotProducts(Product p);	/**	 * 加载促销活动的商品	 * @return	 */	List<Product> selectActivityProductList(Product p);	/**	 * 用户浏览的商品信息存储在session中	 * 由于存储的数量有限，每一个sessin中只存储最近的10个商品，并且只存储一些基本的信息,如：商品ID、商品名称、现价、原价。	 * 这里需要用到数量固定的缓存策略，最后浏览的商品在第一个位置	 */	void saveHistoryProductToSession(Product e);	/**	 * 根据商品ID检查商品信息	 * @return	 */	Catalog checkProduct(Product e);	/**	 * 商品详情页面，图片列表的处理	 */	public void productImagesBiz(String imagesStr, Product e) ;}